mapper.insert:

INSERT INTO users (
          email
        , password
        , nickname
        , createdDate
        , modifiedDate
        ) 
      VALUES 
        (
          #{email}
        , #{password}
        , #{nickname}
        , now()
        , #{modifiedDate}
        )

mapper.select:
SELECT *
  FROM users u 
 WHERE u.email = #{email}
